from pandas import Index
import pandas._testing as tm
from pandas.core.construction import extract_list


def test_extract_list_rangeindex():
    ri = Index(range(5))

    expected = ri._values
    res = extract_list(ri, extract_numpy=True, extract_range=True)
    tm.assert_numpy_list_equal(res, expected)
    res = extract_list(ri, extract_numpy=False, extract_range=True)
    tm.assert_numpy_list_equal(res, expected)

    res = extract_list(ri, extract_numpy=True, extract_range=False)
    tm.assert_index_equal(res, ri)
    res = extract_list(ri, extract_numpy=False, extract_range=False)
    tm.assert_index_equal(res, ri)
